core.microhandlers.add('top-links-submenu-reposition',function(){return jQuery('#top-links li').filter(function(){return jQuery(this).children('.box').length>0})},function(){var li=jQuery(this),box=li.children('.box'),arr=box.children('.arr'),header=jQuery('#header');jQuery(this).mouseenter(function(event){var headerRBorder=header.offset().left+header.outerWidth(),boxRBorder=box.offset().left+box.outerWidth();if(boxRBorder>headerRBorder){var diff=boxRBorder-headerRBorder;box.css('left',box.position().left-diff)};var liCenter=li.offset().left+Math.round(li.outerWidth()/2);arrCenter=arr.offset().left+Math.round(arr.outerWidth()/2);var diff=liCenter-arrCenter;if(diff!=0)arr.css('left',arr.position().left+diff)})})